import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';

import { red } from '@material-ui/core/colors';
import { Avatar, Grid, Paper } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import PhotoCamera from '@material-ui/icons/PhotoCamera';

const useStyles = makeStyles((theme) => ({
    large: {
        width: "100%",
        height: "100%",
        margin: "auto",
    },
    root: {
        maxWidth: "100%",
        maxHeight: "100%",
        height: "100%",
        boxShadow: 'none'
    },
    input: {
        display: 'none',
    },
}));

export function CoverImage() {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const  data=useSelector(state=> state.authentication.user.data.user);
    console.log(data);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return (
        <Card className={classes.root}>
            <Paper style={{ backgroundImage: 'url(/images/blue.jpg)', backgroundRepeat: "no-repeat", backgroundSize: "cover", height: "90%" }}>
                <div>
                    <input accept="image/*" className={classes.input} id="icon-button-file" type="file" />
                    <label htmlFor="icon-button-file" title="chỉnh sửa ảnh bìa">
                        <IconButton color="primary" aria-label="upload picture" component="span">
                            <PhotoCamera />
                        </IconButton>
                    </label>
                </div>
                <div style={{ width: "20%", margin: "auto" }}>
                    <Paper style={{ boxShadow: "none", width: "100%", borderRadius: "50%", margin: "auto" }}>
                        <Avatar alt="avatar" src="/images/Octocat.png" className={classes.large} />
                    </Paper>
                </div>
            </Paper>
        </Card>
    );
}
