import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import { red } from '@material-ui/core/colors';
import Tabs from './Tabs';
import { Chip, Divider } from '@material-ui/core';
import { Modal, Button } from 'antd';
import styles from './styles.module.css';
import { Form, Input } from 'antd';
import WorkIcon from "@material-ui/icons/Work";
import BeachAccessIcon from "@material-ui/icons/BeachAccess";
import VideocamIcon from '@material-ui/icons/Videocam';
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import LocationOnIcon from '@material-ui/icons/LocationOn';
import { Upload } from 'antd';
import axios from 'axios';
import {authHeader}  from './../../helpers/auth-header';
import Location from './Location';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        marginTop: "100px",
        marginBottom: "1%"
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: "white",
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    itemIcon: {
        width: "20%",
        float: "left",
    },
    formItem: {
        marginBottom: '0px'
    }
}));

const header=authHeader();
let config = {
  headers: header
}
let params = {
    description: "description xxx 123",
    photo: "photo",
    hashtags: ["dung", "trunog"],
    location: { 
        type: "map",
        coordinates: [1, 2],
        address: "QT"
    },
    tags: ["dung", "trunog"]
  }

export function WhatDoYouThing() {
    const classes = useStyles();
    const layout = {
        labelCol: { span: 8 },
        wrapperCol: { span: 16 },
    };
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    const [expanded, setExpanded] = React.useState(false);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };
    
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [isLocationVisible, setIsLocationVisible] = useState(false);
    const onFinish = (values) => {
        console.log(values);
        axios.post(`http://localhost:8000/api/posts`,params, config)
        .then(res => 
            res.data.data.data
        ).catch(error => console.log(error));
    };
    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };
    const onPreview = async file => {
        let src = file.url;
        if (!src) {
            src = await new Promise(resolve => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
            });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow.document.write(image.outerHTML);
    };
    const [fileList, setFileList] = useState([]);

    const onChange = ({ fileList: newFileList }) => {
        setFileList(newFileList);
        console.log(newFileList.status)
    };

    return (
        <Card className={styles.root}>
            <CardHeader
                avatar={
                    <Avatar aria-label="recipe" className={classes.avatar}>
                        T
          </Avatar>
                }
                subheader={
                    <Chip
                        variant="outlined"
                        label="Bạn Đang Nghĩ Gì?"
                        style={{
                            width: "100%",
                            justifyContent: "left"
                        }}
                        clickable
                        onClick={showModal}
                    >

                    </Chip>
                }
            />

            <Divider style={{ width: "96%", margin: 'auto' }}></Divider>
            <CardActions disableSpacing className={styles.post} style={{ height: "40%" }}>
                <Tabs></Tabs>
            </CardActions>
            <Modal title="Tạo bài viết"
                visible={isModalVisible}
                onOk={handleOk}
                onCancel={handleCancel}
                footer={[null]}
            >
                <CardHeader
                    avatar={
                        <Avatar aria-label="recipe" src={avatarImg} className={classes.avatar}>
                        </Avatar>
                    }
                    title="Shrimp and Chorizo Paella"
                    subheader="September 14, 2016"
                    style={{ padding: '0' }}
                />
                <Form style={{ marginTop: '20px' }} onFinish={onFinish}>
                    <Form.Item className={classes.formItem} name={['user', 'introduction']}>
                        <Input.TextArea />
                    </Form.Item>
                    <Upload
                        action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                        listType="picture-card"
                        fileList={fileList}
                        onChange={onChange}
                        onPreview={onPreview}
                    >
                        {'+ Upload'}
                    </Upload>
                    <Form.Item className={classes.formItem}  >
                        <List>
                            <ListItem button className={classes.itemIcon}>
                                <ListItemAvatar>
                                    <VideocamIcon color="inherit"></VideocamIcon>
                                </ListItemAvatar>
                            </ListItem>
                            <ListItem button className={classes.itemIcon}>
                                <ListItemAvatar>
                                    <WorkIcon />
                                </ListItemAvatar>
                            </ListItem>
                            <ListItem button className={classes.itemIcon}>
                                <ListItemAvatar>
                                    <BeachAccessIcon />
                                </ListItemAvatar>
                            </ListItem>
                            <ListItem button className={classes.itemIcon} onClick={()=>{setIsLocationVisible(true)}}>
                                <ListItemAvatar>
                                    <LocationOnIcon />
                                </ListItemAvatar>
                            </ListItem>
                        </List>
                    </Form.Item >
                    <Form.Item className={classes.formItem}  >
                        <Button type="primary" block size='large' htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                    
                </Form>
                <Card>
                </Card>
            </Modal>
            {
                isLocationVisible==true?<Location></Location>:setIsModalVisible(false)
            }
        </Card>

    );
}
