import React, { useEffect, useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import { v4 as uuidv4 } from 'uuid';
import {
    ConversationHeader,
    Avatar,
    VoiceCallButton,
    VideoCallButton,
    ChatContainer,
    TypingIndicator,
    MessageSeparator,
    EllipsisButton,
    MessageList,
    Message,
    MessageInput,
} from "@chatscope/chat-ui-kit-react";
import { useDispatch, useSelector } from 'react-redux';
import { chatAction } from '../../actions/chat.action';
import { chat } from '../../reducers/chat.reducer';
// import { MessageInput } from './messageInput';
const useStyles = makeStyles((theme) => ({
    formMessage: {
        height: "750px",
        position: "relative"
    },
    nonePadding: {
        padding: "0!important"
    }
}));

export function MessContent(props) {
    const [messageInputValue, setMessageInputValue] = useState("");
    const dispatch = useDispatch();
    const userId = useSelector(state => state.chat.currentRoom.members[0]._id);
    const profilePicture = useSelector(state => state.chat.currentRoom.members[0].photo);
    const callingModal = useSelector(state => state.chat.callingModal);
    const socket = useSelector(state => state.socket.socket);
    const currentRoom = useSelector(state => state.chat.currentRoom);
    const roomId = useSelector(state => state.chat.roomId);
    const content = props.content;
    function onSubmitMessage(val) {
        let receiver=currentRoom.members[0]._id!=JSON.parse(localStorage.getItem('user')).data.user._id?currentRoom.members[0]._id:currentRoom.members[1]._id
        console.log({
            receiver: receiver,
            val,
            roomId,
            sender: userId,
            uuid: uuidv4()
        })
        if (val !== '') {
            dispatch(chatAction.sendMessage({
                receiver: currentRoom.user,
                val,
                roomId,
                sender: userId,
                uuid: uuidv4()
            }))
        }
    }

    function fetchMessage() {
        console.log(content)
        // const lastId=content.messages[0]._id;
        dispatch(chatAction.getMessagesForRoom(
            {
                ...currentRoom,
                // lastId,
                initialFetch: false
            }
        ))
    }

    useEffect(() => {
        //get message
        //send readMessage
        //
        fetchMessage()
    }, [])
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    return (
        <ChatContainer>
            <ConversationHeader>
                <ConversationHeader.Back />
                <Avatar src={avatarImg} name="Zoe" />
                <ConversationHeader.Content userName="Zoe" info="Active 10 mins ago" />
                <ConversationHeader.Actions>
                    <VoiceCallButton />
                    <VideoCallButton />
                    <EllipsisButton orientation="vertical" />
                </ConversationHeader.Actions>
            </ConversationHeader>
            <MessageList typingIndicator={<TypingIndicator content="Zoe is typing" />}>
                <MessageSeparator content="Saturday, 30 November 2019" />
                <Message model={{
                    message: "Hello my friend",
                    sentTime: "15 mins ago",
                    sender: "Zoe",
                    direction: "incoming",
                    position: "single"
                }}>
                    <Avatar src={avatarImg} name="Zoe" />
                </Message>
                <Message model={{
                    message: "Hello my friend",
                    sentTime: "15 mins ago",
                    sender: "Patrik",
                    direction: "outgoing",
                    position: "single"
                }} avatarSpacer />
                <Message model={{
                    message: "Hello my friend",
                    sentTime: "15 mins ago",
                    sender: "Zoe",
                    direction: "incoming",
                    position: "first"
                }} avatarSpacer />
            </MessageList>
            <MessageInput placeholder="Type message here" value={messageInputValue} onChange={val => setMessageInputValue(val)} onSend={(val) => onSubmitMessage(val)} />
        </ChatContainer>
    );
}
