import React, { useEffect, useState } from 'react';
import 'antd/dist/antd.css';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { SideBar } from './../../components/sidebar/Sidebar';
import { Mess } from './../../components/sidebar/Mess';
import { Post } from '../../components/post/Post';
import { Container } from '@material-ui/core';
import {WhatDoYouThing} from './../../components/whatdoyouthing/WhatDoYouThing';
import {authHeader}  from './../../helpers/auth-header';
import {postService} from './../../services/post.service';
import { useDispatch, useSelector } from 'react-redux';
import {postAction} from './../../actions/post.action';
import axios from 'axios';
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  nonePadding: {
    padding: "0!important"
  }
}));

export function HomePage() {
  const classes = useStyles();
  const  data=useSelector(state=> state.post);
  const dispatch=useDispatch();

  let mounted = true;
  let header=authHeader();
  let config = {
    headers: header
  }
  useEffect(() => {
    postService.getAllPost()
     .then(items => {
       if(mounted) {
          dispatch();
       }
     })
    return () => mounted = false;
  },[]);

  return (
    <Container>
      <Grid container spacing={3} >
        <Grid item xs={12} className={classes.nonePadding}>
          <Header></Header>
        </Grid>
        <Grid item xs={3}>
          <SideBar></SideBar>
        </Grid>
        <Grid item xs={6}>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <WhatDoYouThing></WhatDoYouThing>
          { 
              
              data?data.map((value) => {
                  return <Post value={value}></Post>;
               }):null             
          }
        </Grid>
        <Grid item xs={3}>
          <Mess></Mess>
        </Grid>
      </Grid>
    </Container>
  );
}
