import React, { useEffect, useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { Post } from '../../components/post/Post';
import { TabList } from '../../components/profileTabList/TabList';
import { CoverImage } from '../../components/coverImage/CoverImage';
import { Container, Divider } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import { userService } from './../../services/user.service';
import { userActions } from './../../actions/user.actions';
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    nonePadding: {
        padding: "0!important"
    }
}));

export function Profile(props) {
    const [idUser, setIdUser] = useState(props.match.params.id);
    const classes = useStyles();
    const [userInfo, setUserInfo] = useState(props.location.state.userInfo);
    const [isFollow, setIsFollow] = useState(false);
    function getInfo(){
        userService.getInfoUser(idUser)
            .then(items => {
                    setUserInfo(items.data.data);
            }).catch(err => console.log(err))
    }
    useEffect(() => {
        // getInfo();
    },[isFollow]);
    return (
        <Container>
            <Grid container spacing={3} >
                <Grid item xs={12} className={classes.nonePadding}>
                    <Header></Header>
                </Grid>
                <Grid container style={{ marginTop: "100px" }}>
                    <Grid item xs={12} style={{ marginBottom: "1vh" }} >
                        <CoverImage userInfo={userInfo}></CoverImage>
                    </Grid>
                    <Grid container>
                        <Grid xs={12}>
                            <div style={{ margin: 'auto', width: '100%' }}>
                                <h1 style={{ display: 'flex', justifyContent: 'center', marginBottom: "1%" }}>{userInfo ? userInfo.user.firstname + " " + userInfo.user.lastname : ''}</h1>
                            </div>
                        </Grid>
                        <Grid xs={12}>
                            <TabList userInfo={userInfo} idUser={idUser} follow={[isFollow, setIsFollow]}></TabList>
                        </Grid>

                    </Grid>
                    <Grid item xs={4} style={{ padding: "1%" }}>
                        {/* <Post></Post> */}
                    </Grid>
                    <Grid item xs={8} style={{ padding: "1%" }}>

                    </Grid>

                </Grid>
            </Grid>
        </Container >
    );
}
