import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import ChatBubble from '@material-ui/icons/ChatBubble';
import Home from '@material-ui/icons/Home';
import Groups from '@material-ui/icons/Group';
import Store from '@material-ui/icons/Store';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { useLocation } from 'react-router';
import { history } from './../../helpers/history';
import { useDispatch, useSelector } from 'react-redux';
import { menuActions } from '../../actions/menu.action';
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-prevent-tabpanel-${index}`}
      aria-labelledby={`scrollable-prevent-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `scrollable-prevent-tab-${index}`,
    'aria-controls': `scrollable-prevent-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  noneShawdow: {
    boxShadow: "none",
  },
}));

export default function ScrollableTabsButtonPrevent() {
  const classes = useStyles();

  const location = useLocation();
  var menuNumber=0;
  var pathname=location.pathname;
  if(pathname.startsWith('/home')){
    menuNumber=0
  }else if(pathname.startsWith('/message')){
    menuNumber=1
  }else if(pathname.startsWith('/profile')){
    menuNumber=2
  }else if(pathname.startsWith('/market')){
    menuNumber=3
  }
  var menu=useSelector(state=>state.menu.menu);
  menuNumber=menu==undefined?menuNumber:menu;
  const [value, setValue] = useState(menuNumber);
  
  const dispatch = useDispatch();

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Tabs
      value={value}
      onChange={handleChange}
      // variant="scrollable"
      // scrollButtons="on"
      aria-label=""
      centered
    >
      <Tab icon={<Home />} aria-label="Home" {...a11yProps(0)} onClick={()=>{history.push('/home');dispatch(menuActions.change(0));}}/>
      <Tab icon={<ChatBubble />} aria-label="Message" {...a11yProps(1)} onClick={()=>{history.push('/message');dispatch(menuActions.change(1)); }}/>
      <Tab icon={<Groups />} aria-label="Groups" {...a11yProps(2)} onClick={()=>{history.push('/profile'); dispatch(menuActions.change(2));}}/>
      <Tab icon={<Store />} aria-label="Market Place" {...a11yProps(3)} onClick={()=>{history.push('/market');dispatch(menuActions.change(3)); }}/>
    </Tabs>
  );
}
