import React, { useState } from 'react';
import { render } from 'react-dom';
export default class Gmap extends React.Component{
    componentDidMount (){
        // trỏ global function window.initMap này vào hàm initMap của component để thằng google có thể  gọi trong hàm callback
        window.initMap = this.initMap;
    
        // chèn ngay đoạn js của googleapis cho anh xài ngay.
        loadJS('https://maps.googleapis.com/maps/api/js?key=AIzaSyCZz4jRnJiJ1FMXJbHwdy18jaHzoPuYT84&callback=initMap')
    }
    
    initMap = () => {
        var map = new google.maps.Map(this.refs.map.getDOMNode(), { /* các options khác*/});
    }
    render(){
        return <div ref="map" style={{width:"100%", height: "100%"}}></div>;
    }
}
// hàm này để chèn thêm <script /> sau khi gọi
function loadJS(src) {
    var ref = window.document.getElementsByTagName("script")[0];
    var script = window.document.createElement("script");
    script.src = src;
    script.async = true;
    ref.parentNode.insertBefore(script, ref);
}
