import { notificationConstants } from "../constants/notification.constants";
import { notificatonService } from "../services/notification.service";

export const notificationActions = {
    toggleNotificationPopup,
    closeNotificationPopup,
    addNotification,
    fetchNotifications
}

function readNotifications(notificationIds) {
    notificatonService.readNotifications(notificationIds)
        .then((res) => console.log(res))
        .catch(err => console.log(err));
}

function fetchNotifications(queryOptions, notificationIds) {
    return dispatch => {
        notificatonService.fetchNotifications(queryOptions).then(
            response => {
