import React from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Badge from '@material-ui/core/Badge';
import { red } from '@material-ui/core/colors';
import { Avatar, Grid, Paper } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import PhotoCamera from '@material-ui/icons/PhotoCamera';

const useStyles = makeStyles((theme) => ({
    large: {
        width: "100%",
        height: "100%",
        margin: "auto",
    },
    root: {
        maxWidth: "100%",
        maxHeight: "100%",
        height: "100%",
        boxShadow: 'none'
    },
    input: {
        display: 'none',
    },
}));
const SmallAvatar = withStyles((theme) => ({
    root: {
        width: "25%",
        height: "25%",
        border: `2px solid ${theme.palette.background.paper}`,
    },
}))(Avatar);
export function CoverImage(props) {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const data = props.userInfo;
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return (
        <Card className={classes.root}>
            <Paper style={{ backgroundImage: 'url(https://res.cloudinary.com/elca/image/upload/v1618641272/gldei3c63jv9xewpzhmx.jpg?fbclid=IwAR33-qOZee0FVN4PcJfrJPZRmY9_KgIEZa3us6d8Ks6LFM6wRMdqFk2pLx8)', backgroundRepeat: "no-repeat", backgroundSize: "cover", height: "90%", postion: "relative" }}>
                <div>
                    <input accept="image/*" className={classes.input} id="icon-button-file" type="file" />
                    <label htmlFor="icon-button-file" title="chỉnh sửa ảnh bìa">
                        <IconButton color="primary" aria-label="upload picture" component="span">
                            <PhotoCamera />
                        </IconButton>
                    </label>
                </div>
                <div style={{ width: "20%", top: "0", margin: "auto", paddingTop: "3%" }}>
                    <Paper style={{ boxShadow: "none", width: "100%", borderRadius: "50%", margin: "auto" }}>
                        <Badge
                            overlap="circle"
                            anchorOrigin={{
                                vertical: 'bottom',
                                horizontal: 'right',
                            }}
                            badgeContent={<SmallAvatar alt="Change avatar">
                                <IconButton color="primary" aria-label="upload picture" component="span">
                                    <PhotoCamera />
                                </IconButton>
                            </SmallAvatar>}
                        >
                            <Avatar alt="" src={data ? data.photo : null} className={classes.large} />
                        </Badge>
                    </Paper>
                </div>
            </Paper>
        </Card>
    );
}
