import React,{useState} from 'react';
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { Divider, Grid } from '@material-ui/core';
import FriendList from '../../components/friendList/FriendList';
import { WhatDoYouThing } from '../../components/whatdoyouthing/WhatDoYouThing';
import { Button } from 'antd';
import { userService } from './../../services/user.service';
import {postService} from './../../services/post.service';
import {AllPost} from './AllPost'
const useStyles = makeStyles((theme) => ({

}));
function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`scrollable-prevent-tabpanel-${index}`}
            aria-labelledby={`scrollable-prevent-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `scrollable-prevent-tab-${index}`,
        'aria-controls': `scrollable-prevent-tabpanel-${index}`,
    };
}



export function TabList(props) {
    const dataUserFollow = props.userInfo;
    const classes = useStyles();
    const [value, setValue] = React.useState(0);
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };
    const dataUserCurrent = useSelector(state => state.authentication.user.data.user);
    
    console.log(dataUserCurrent);
    console.log(dataUserFollow);
    if (dataUserCurrent._id === (dataUserFollow?dataUserFollow._id:'')) {
        var isCurrentUser = true;
    }
    const [isFollow,setIsFollow] = useState(false);
    const followUser =() =>{
        userService.follow(dataUserFollow._id);
        setIsFollow(true)
    }
    
    return (
        <div className={classes.root}>
            <Grid container>
                <Grid item xs={8} style={{ padding: "1%" }}>
                    <Tabs
                        value={value}
                        onChange={handleChange}
                        scrollButtons="off"
                        aria-label="scrollable prevent tabs example"
                        centered
                        variant="fullWidth"
                        style={{
                            width: "100%"
                        }}
                    >
                        <Tab sty label="Bài viết" {...a11yProps(0)} />
                        <Tab label="Giới thiệu" {...a11yProps(1)} />
                        <Tab label="Bạn bè" {...a11yProps(2)} />
                        <Tab label="Ảnh" {...a11yProps(3)} />

                    </Tabs>
                </Grid>
                <Grid item xs={4} style={{ padding: "1%" }}>
                    {isCurrentUser ? '' : (isFollow ? <Button  style={{ float: 'right' }}>Đang theo dõi</Button> : <Button style={{ float: 'right' }} onClick={followUser} type="primary">Theo dõi</Button>)}
                </Grid>

                <TabPanel value={value} index={0}>
                    <Grid container>
                        <Grid item xs={4} style={{ padding: "1%" }}>

                        </Grid>
                        <Grid item xs={8} style={{ padding: "1%" }}>
                            <AllPost idUser={dataUserFollow?dataUserFollow._id:''}></AllPost>



                        </Grid>
                    </Grid>
                </TabPanel>

                <TabPanel value={value} index={1}>
                    Item Two
        </TabPanel>
                <TabPanel value={value} index={2}>
                    Item Three
        </TabPanel>

            </Grid>
        </div>

    );
}
