import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';

import { red } from '@material-ui/core/colors';
import { Avatar, Grid, Paper } from '@material-ui/core';
import { useSelector } from 'react-redux';

const useStyles = makeStyles((theme) => ({
    large: {
        position: "relative",
        width: "100%",
        height: "100%",
        margin: "auto",

    },
    root: {
        maxWidth: "100%",
        maxHeight: "100%",
        boxShadow: 'none'
    },
    media: {
        height: 0,
        // paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

export function CoverImage() {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const  data=useSelector(state=> state.authentication.user.data.user);
    console.log(data);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return (
        <Card className={classes.root}>
            <Paper style={{ backgroundImage: 'url(https://res.cloudinary.com/elca/image/upload/v1618641272/gldei3c63jv9xewpzhmx.jpg?fbclid=IwAR33-qOZee0FVN4PcJfrJPZRmY9_KgIEZa3us6d8Ks6LFM6wRMdqFk2pLx8)' , backgroundRepeat: "no-repeat",backgroundSize: "cover", height: "90%", postion: "relative" }}>
                <div style={{ width: "20%", top: "0", margin: "auto", paddingTop: "3%" }}>
                    <Paper style={{ boxShadow: "none", width: "100%", borderRadius: "50%", margin:"auto" }}>
                        <Avatar alt="" src={data?data.photo:null} className={classes.large} />
                    </Paper>
                </div>
            </Paper>

        </Card>
    );
}
