import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import { red } from '@material-ui/core/colors';
import Tabs from './Tabs';
import { Chip, Divider } from '@material-ui/core';
import { Modal, Button } from 'antd';
import styles from './styles.module.css';
import { Form, Input } from 'antd';
import WorkIcon from "@material-ui/icons/Work";
import BeachAccessIcon from "@material-ui/icons/BeachAccess";
import VideocamIcon from '@material-ui/icons/Videocam';
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import { Upload } from 'antd';
import axios from 'axios';
import { authHeader } from './../../helpers/auth-header';
import { PlusOutlined } from '@ant-design/icons';
import LocationOn from '@material-ui/icons/LocationOn';
import Location from './Location';
import getBase64 from './../../utils/getBase64'
const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "100%",
        marginTop: "100px",
        marginBottom: "1%"
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: "white",
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    itemIcon: {
        width: "20%",
        float: "left",
    },
    formItem: {
        marginBottom: '0px'
    }
}));


let params = {
    description: "description xxx 123",
    photo: "photo",
    hashtags: ["dung", "trunog"],
    location: {
        type: "map",
        coordinates: [1, 2],
        address: "QT"
    },
    tags: ["dung", "trunog"]
}


export function WhatDoYouThing() {

    const classes = useStyles();
    const layout = {
        labelCol: { span: 8 },
        wrapperCol: { span: 16 },
    };
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    const [expanded, setExpanded] = React.useState(false);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    const location = useSelector(state => state.location);
    const user = useSelector(state => state.authentication.user);

    const [isVisible, setIsVisible] = useState(false);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [previewVisible, setPreviewVisible] = useState(false);
    const [previewImage, setPreviewImage] = useState('');
    const [previewTitle, setPreviewTitle] = useState('false');

    const onFinish =async (values) => {
        let header = authHeader();
        let config = {
            headers: header
        }
        const data = new FormData();
        data.append('description', values.user.introduction);
        for(const file of fileList){
            let fileData= await getBase64(file.originFileObj);
            let blod=new Blob([fileData], { type: 'image/png'});
            data.append('files',blod , file.name);
        }
        data.append('hashtags', []);
        data.append('location', null);
        data.append('tags', ["c", "d"]);
        axios.post(`http://localhost:8000/api/posts`, data, config)
            .then(res =>
                setIsModalVisible(false)
            ).catch(error => console.log(error));
    };
    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };
    const handleCancelPreview = () => setPreviewVisible(false);
    const onPreview = async file => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        setPreviewImage(file.url || file.preview);
        setPreviewVisible(true);
        setPreviewTitle(file.name || file.url.substring(file.url.lastIndexOf('/') + 1))
    };
    const [fileList, setFileList] = useState([]);

    const onChange = ({ fileList }) => {
        setFileList(fileList);
    };
    const uploadButton = (
        <div>
            <PlusOutlined />
            <div style={{ marginTop: 8 }}>Upload</div>
        </div>
    );
    const date = new Date().toLocaleString();
    return (
        <Card className={styles.root}>
            <CardHeader
                avatar={
                    <Avatar aria-label="recipe" className={classes.avatar}>

                    </Avatar>
                }
                subheader={
                    <Chip
                        variant="outlined"
                        label={user.data.user.firstname + ", Bạn Đang Nghĩ Gì?"}
                        style={{
                            width: "100%",
                            justifyContent: "left"
                        }}
                        clickable
                        onClick={showModal}
                    >

                    </Chip>
                }
            />

            <Divider style={{ width: "96%", margin: 'auto' }}></Divider>
            <CardActions disableSpacing className={styles.post} style={{ height: "40%" }}>
                <Tabs></Tabs>
            </CardActions>
            <Modal title="Tạo bài viết"
                visible={isModalVisible}
                onOk={handleOk}
                onCancel={handleCancel}
                footer={[null]}
            >
                <CardHeader
                    avatar={
                        <Avatar aria-label="recipe" src={avatarImg} className={classes.avatar}>
                        </Avatar>
                    }
                    title={user.data.user.firstname}
                    subheader={date}
                    style={{ padding: '0' }}
                />
                <Form style={{ marginTop: '20px' }} onFinish={onFinish}>
                    <Form.Item className={classes.formItem} name={['user', 'introduction']}>
                        <Input.TextArea />
                    </Form.Item>
                    <Upload
                        listType="picture-card"
                        fileList={fileList}
                        onPreview={onPreview}
                        onChange={onChange}
                        beforeUpload={file => {
                            return false;
                        }}
                        multiple
                    >
                        {fileList.length >= 8 ? null : uploadButton}
                    </Upload>
                    <Modal
                        visible={previewVisible}
                        title={previewTitle}
                        footer={null}
                        onCancel={handleCancelPreview}
                    >
                        <img alt="example" style={{ width: '100%' }} src={previewImage} />
                    </Modal>
                    <Form.Item className={classes.formItem}  >
                        <List >
                            <ListItem button className={classes.itemIcon}>
                                <ListItemAvatar>
                                    <VideocamIcon color="inherit"></VideocamIcon>
                                </ListItemAvatar>
                            </ListItem>
                            <ListItem button className={classes.itemIcon}>
                                <ListItemAvatar>
                                    <WorkIcon />
                                </ListItemAvatar>
                            </ListItem>
                            <ListItem button className={classes.itemIcon}>
                                <ListItemAvatar>
                                    <BeachAccessIcon />
                                </ListItemAvatar>
                            </ListItem>
                            <ListItem button className={classes.itemIcon} onClick={(e) => { setIsVisible(true) }}>
                                <ListItemAvatar>
                                    <LocationOn />
                                </ListItemAvatar>
                            </ListItem>
                        </List>
                    </Form.Item >
                    <Form.Item className={classes.formItem}  >
                        <Button type="primary" block size='large' htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
                <Card>
                </Card>
                <Location isVisible={isVisible} setIsVisible={setIsVisible}>
                </Location>
            </Modal>
        </Card>

    );
}
