import React, { useEffect, useState } from 'react';
import 'antd/dist/antd.css';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { SideBar } from './../../components/sidebar/Sidebar';
import { Mess } from './../../components/sidebar/Mess';
import { Post } from '../../components/post/Post';
import { Container } from '@material-ui/core';
import { WhatDoYouThing } from './../../components/whatdoyouthing/WhatDoYouThing';
import { authHeader } from './../../helpers/auth-header';
import { postService } from './../../services/post.service';
import { useDispatch, useSelector } from 'react-redux';
import { postActions } from './../../actions/post.action';
import axios from 'axios';
import { socketAction } from '../../actions/socket.action';
import "./home.css"
import RestaurantRecommend from '../../components/restaurantRecommend/RestaurantRecommend';
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  nonePadding: {
    padding: "0!important"
  },
  rightBar: {
    marginTop: '86px',
    marginLeft: '70px'
  }

}));

export function HomePage() {
  const classes = useStyles();
  //state redux
  const data = useSelector(state => state.post.postList);
  const dispatch = useDispatch();

  let mounted = true;
  useEffect(() => {
    postService.getAllPost()
      .then(items => {
        console.log(items);
        dispatch(postActions.getAllPost(items.data.data.data))
      }
      ).catch(err => console.log(err));
    // dispatch(socketAction.connect());
    return () => mounted = false;
  }, []);

  return (
    // <Container style={{ backgroundColor: "#FAFAFA" }}>
    <Grid container spacing={0} >
      <Grid item xs={12} className={classes.nonePadding}>
        <Header></Header>
      </Grid>
      <Grid item md={2} className="sideBar">
        <SideBar ></SideBar>
      </Grid>
      <Grid item md={1}>
      </Grid>
      <Grid item md={6}>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <WhatDoYouThing></WhatDoYouThing>
        {
          data ? data.map((value) => {
            return <Post value={value} key={value._id} ></Post>;
          }) : null
        }
      </Grid>
      <Grid item md={2} className={classes.rightBar}>
        <RestaurantRecommend ></RestaurantRecommend>
      </Grid>

    </Grid>
    // </Container>
  );
}
