import "mapbox-gl/dist/mapbox-gl.css";
import "react-map-gl-geocoder/dist/mapbox-gl-geocoder.css";
import React, { useState, useRef, useCallback } from "react";
import MapGL, { Marker,NavigationControl,FullscreenControl } from "react-map-gl";
import Geocoder from "react-map-gl-geocoder";

// Please be a decent human and don't abuse my Mapbox API token.
// If you fork this sandbox, replace my API token with your own.
// Ways to set Mapbox token: https://uber.github.io/react-map-gl/#/Documentation/getting-started/about-mapbox-tokens
const MAPBOX_TOKEN =
	'pk.eyJ1IjoidG9hbmRvIiwiYSI6ImNrbThscTh4cjBrNnQycHF5MWJqd29jaDQifQ.LNubsbmqGfZhCCRymL-A2g';

export default function GMap() {

	const [viewport, setViewport] = useState({
		latitude: 37.7577,
		longitude: -122.4376,
		zoom: 15
	});
	const navStyle = {
		position: "absolute",
		top: 36,
		left: 0,
		padding: "10px"
	  };
	  const fullscreenControlStyle = {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "10px"
	  };
	function onSelected(viewport, item) {
		setViewport({
			viewport
		})
	}
	const mapRef = useRef();
	const handleViewportChange = useCallback(
		(newViewport) => setViewport(newViewport),
		[]
	);
	navigator.geolocation.getCurrentPosition(
		function (position) {
			setViewport({
				latitude: position.coords.latitude,
				longitude: position.coords.longitude,
				zoom: 15
			})
		},
		function (error) {
			console.error("Error Code = " + error.code + " - " + error.message);
		}
	);
	const handleGeocoderViewportChange = useCallback(
		(newViewport) => {
			const geocoderDefaultOverrides = { transitionDuration: 1000 };

			return handleViewportChange({
				...newViewport,
				...geocoderDefaultOverrides
			});
		},
		[handleViewportChange]
	);
	function makerOnClick(e) {
		console.log(e);
		setViewport({
			latitude: e.lngLat[1],
			longitude: e.lngLat[0],
			zoom: 15
		})
	}
	const onMarkerClick = (event) => {
		alert('You clicked on marker');
		event.stopPropagation();
	};
	return (
		<div style={{ height: "100vh" }}>
			<MapGL
				ref={mapRef}
				{...viewport}
				width="100%"
				height="100%"
				onViewportChange={handleViewportChange}
				mapboxApiAccessToken={MAPBOX_TOKEN}
				onClick={e => makerOnClick(e)}
			>
				<Geocoder
					mapRef={mapRef}
					onViewportChange={handleGeocoderViewportChange}
					mapboxApiAccessToken={MAPBOX_TOKEN}
					onSelected={onSelected}
					position="top-left"
				/>
				<Marker
					key="1"
					longitude={viewport.longitude}
					latitude={viewport.latitude}
					onClick={onMarkerClick}
				>
					 <div >Click me! âœ¨</div>
				</Marker>
			</MapGL>
		</div>
	);
};
