import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {
    Form,
    Input,
    Button
} from 'antd';
import {userService} from './../../services/user.service'
import { message, Space } from 'antd';
const useStyles = makeStyles();


const onFinish = (values) => {  
    userService.changePassword(values.currentPassword,values.newPassword,values.confirmPassword)
    .then(response=>{
        console.log(response);
        if(response){
            
            message.success('This is a success message');
        }
        else {
            message.error('Đổi mật khẩu thất bại! Bạn vui lòng kiểm tra lại!');
        }
    })
    
};
export default function ChangePassword() {
    const classes = useStyles();
    const formItemLayout = {
        labelCol: {
            xs: { span: 24 },
            sm: { span: 8 },
        },
        wrapperCol: {
            xs: { span: 24 },
            sm: { span: 16 },
        },
    };
   
    return (
        
        <Form
            {...formItemLayout}
            name="register"
            onFinish={onFinish}
            initialValues={{
                residence: ['zhejiang', 'hangzhou', 'xihu'],
                prefix: '86',
            }}
            scrollToFirstError
        >
            <Form.Item
                name="currentPassword"
                label="Mật khẩu hiện tại"
                rules={[
                    {
                        required: true,
                        message: 'Vui lòng nhập mật khẩu của bạn!',
                    },
                ]}
            >
                <Input.Password />
            </Form.Item>

            <Form.Item
                name="newPassword"
                label="Mật khẩu mới"
                rules={[
                    {
                        required: true,
                        message: 'Vui lòng nhập mật khẩu mới!',
                    },
                ]}
                hasFeedback
            >
                <Input.Password />
            </Form.Item>

            <Form.Item
                name="confirmPassword"
                label="Xác nhận mật khẩu"
                dependencies={['newPassword']}
                hasFeedback
                rules={[
                    {
                        required: true,
                        message: 'Vui lòng xác nhận mật khẩu của bạn!',
                    },
                    ({ getFieldValue }) => ({
                        validator(_, value) {
                            if (!value || getFieldValue('newPassword') === value) {
                                return Promise.resolve();
                            }
                            return Promise.reject(new Error('Mật khẩu bạn vừa nhập không khớp!'));
                        },
                    }),
                ]}
            >
                <Input.Password />
            </Form.Item>
            <Form.Item >
                <Button type="primary" htmlType="submit">
                    Đổi mật khẩu
                    
          </Button>
            </Form.Item>
        </Form>
    );
}
