import React, { useState, useEffect } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { SideBarSearch } from './../../components/sidebarSearch/SidebarSearch';
import { Post } from '../../components/post/Post';
import { Card, CardActionArea, CardContent, CardMedia, Container, List, Typography } from '@material-ui/core';
import { postService } from '../../services/post.service';
import { searchService } from '../../services/search.service';
import { history } from '../../helpers/history';
const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    nonePadding: {
        padding: "0!important"
    },
    people: {
        marginTop: '100px'
    },
    title: {
        fontSize: 16,
        fontWeight: 'bold'
    },
    iconMessage: {
        textAlign: 'center',
        paddingTop: '35px'
    },
    restaurantList: {
        width: '47%',
        marginRight: '10px',
        minWidth: '300px',
        marginBottom: '10px',
        height: '300px'
    }, media: {
        height: 140,
    },
    '@global': {
        '#listRestaurant .MuiListItem-gutters': {
            display: "block"
        }
    }
}));

export function Search(props) {
    const dataSearch = props.match.params.description;
    const classes = useStyles();
    const [dataPost, setDataPost] = useState();
    const [restaurantList, setRestaurantList] = useState();
    let mounted = true;
    async function searchRes(searchKey) {
        let data = await searchService.searchRestaurant(searchKey);
        setRestaurantList(data);
    }
    useEffect(() => {
        postService.getSearchPost(dataSearch)
            .then(items => {
                if (mounted) {
                    setDataPost(items);
                }
            })
        searchRes(dataSearch);
        // dispatch(socketAction.connect());
        return () => mounted = false;
    }, []);
    return (
        <Container>
            <Grid container spacing={3} >
                <Grid item xs={12} className={classes.nonePadding}>
                    <Header></Header>
                </Grid>
                <Grid item xs={3}>
                    <SideBarSearch></SideBarSearch>
                </Grid>
                <Grid item xs={8} className={classes.people}>
                    <Typography>Restaurant</Typography>
                  {
                      restaurantList? restaurantList.map(ele=>{
                        return <Card minWidth="100%">
                            <CardContent >
                                <Typography>{ele._source.restaurantname}</Typography>
                            </CardContent>
                        </Card>
                    }):''
                  }
                  <Typography>Post</Typography>
                    {

                        dataPost ? dataPost.map((value) => {
                            return <Post value={value} key={value._id} isShow={false} ></Post>;
                        }) : null
                    }

                    {/* <ImageList></ImageList>
                    <FriendList></FriendList>
                    <RestaurantNearMe></RestaurantNearMe> */}
                </Grid>
            </Grid >
        </Container >
    );
}
