import React, { Fragment, useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Chat from '@material-ui/icons/Chat';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { Popover, Button } from 'antd';
import { Modal } from 'antd';
import { Form, Input } from 'antd';
import { Upload } from 'antd';
import { commentService } from './../../services/comment.service';
import { postService } from './../../services/post.service';
import { Comment } from 'antd';
import { CommentPost } from './../commentPost/CommentPost';
import { authHeader } from './../../helpers/auth-header';
import { postActions } from './../../actions/post.action';
import axios from 'axios';
import {
  MessageInput,
  ConversationList,
  Conversation,
  Avatar
} from "@chatscope/chat-ui-kit-react";
import { GridList, GridListTile } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: "100%",

  },
  media: {
    padding: "2%",
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

function gridImage(imageList) {
  let result = [];
  imageList.forEach(element => {
    result.push({
      img: element,
      title: 'Image',
      author: 'author',
      cols: 1,
    })
  });
  return result;
}

export function Post(props) {
  var dataPost = props.value;
  const tileData = gridImage(dataPost.photo);
  const [dataComment, setDataComment] = useState([]);
  const classes = useStyles();
  const [expanded, setExpanded] = useState(false);
  var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
  const [visible, setVisible] = useState(false);
  const dispatch = useDispatch();
  const postList = useSelector(state => state.post);
  //getcomment
  let mounted = true;
  useEffect(() => {
    commentService.getAllCommentPost(dataPost._id)
      .then(items => {
        if (mounted) {
          mounted = false;
          setDataComment(items);
        }

      })
    return () => mounted = false;
  }, []);
  //modal
  const [isModalVisible, setIsModalVisible] = useState(false);
  const showModal = () => {
    setIsModalVisible(true);
    handleVisibleChange(false);
  };
  const deletePost = () => {
    postService.deletePost(dataPost._id)
      .then(result => {
        dispatch(postActions.deletePost(dataPost._id));
      }).catch((error) => {
        console.log(error);
      })
  }
  const onPreview = async file => {
    let src = file.url;
    if (!src) {
      src = await new Promise(resolve => {
        const reader = new FileReader();
        reader.readAsDataURL(file.originFileObj);
        reader.onload = () => resolve(reader.result);
      });
    }
    const image = new Image();
    image.src = src;
    const imgWindow = window.open(src);
    imgWindow.document.write(image.outerHTML);
  };
  const [fileList, setFileList] = useState([]);

  const onChange = ({ fileList: newFileList }) => {
    setFileList(newFileList);
    console.log(newFileList.status)
  };
  const onFinish = (values) => {
    console.log(values);
  };
  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  //end modal
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };
  const handleVisibleChange = (visible) => {
    setVisible(visible);
  };
  const content = (
    <div>
      <div>
        <a style={{ color: 'black' }} onClick={showModal}>Chỉnh sửa bài viết</a>
      </div>
      <div>
        <a style={{ color: 'black' }} onClick={deletePost}>Xóa bài viết</a>
      </div>
    </div>
  );

  function likePost(e) {
    let header = authHeader();
    let config = {
      headers: header
    }
    let data = {
      postId: dataPost._id
    };
    return axios.post(`http://localhost:8000/api/posts/likePost`, data, config);
  }
  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar aria-label="recipe" src={avatarImg} className={classes.avatar}>
          </Avatar>
        }
        action={
          <Popover
            content={content}
            title="Title"
            trigger="click"
            visible={visible}
            onVisibleChange={handleVisibleChange}
            placement="bottomRight"
          >
            <IconButton aria-label="settings">
              <MoreVertIcon />
            </IconButton>
          </Popover>

        }
        title={dataPost.author.firstname}
        subheader={dataPost.createdAt}
      />
      <CardContent>
        <Typography variant="body2" component="p">
          {dataPost.description}
        </Typography>
      </CardContent>
      <CardMedia
        className={classes.media}
      >
        <GridList cellHeight={160} className={classes.gridList} cols={3}>
          {tileData.map((tile) => (
            <GridListTile key={tile.img} cols={tile.cols || 1} >
              <img src={tile.img} alt={tile.title} />
            </GridListTile>
          ))
          }
        </GridList>
      </CardMedia>

      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites" onClick={(e) => likePost(e)}>
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="add to favorites">
          <Chat />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <div style={{ overflow: "visible" }} >
            <CommentPost idPost={dataPost._id}></CommentPost>
          </div>
        </CardContent>
      </Collapse>
      {/* <Divider /> */}
      <Modal title="Chỉnh sửa bài viết"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
        footer={[null]}
      >
        <CardHeader
          avatar={
            <Avatar aria-label="recipe" src={avatarImg} className={classes.avatar}>
            </Avatar>
          }
          title={dataPost.author.firstname}
          subheader={dataPost.createdAt}
          style={{ padding: '0' }}
        />
        <Form style={{ marginTop: '20px' }} onFinish={onFinish} fields={[
          {
            name: ['introduction'],
            value: dataPost.description,
          }
        ]}>
          <Form.Item className={classes.formItem} name={'introduction'} >
            <Input.TextArea />
          </Form.Item>
          <Upload
            // action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
            listType="picture-card"
            fileList={fileList}
            beforeUpload={file => {
              return false;
            }}
            multiple
          >
            {'+ Upload'}
          </Upload>
          <Form.Item className={classes.formItem}  >
            <Button type="primary" block size='large' htmlType="submit">
              Submit
                        </Button>
          </Form.Item>

        </Form>
        <Card>
        </Card>
      </Modal>

    </Card>
  );
}
