import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import { history } from '../../helpers/history';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { userService } from '../../services/user.service';
import { Fragment } from 'react';
import { PeopleSearch } from '../../components/peopleSearch/PeopleSearch';
import Card from '@material-ui/core/Card';
import Grid from "@material-ui/core/Grid";
import CardContent from '@material-ui/core/CardContent';
import MessageIcon from '@material-ui/icons/Message';
const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        backgroundColor: theme.palette.background.paper,
    },
    inline: {
        display: 'inline',
    },
    nonePadding: {
        padding: "0!important"
    },
    title: {
        fontSize: 16,
        fontWeight: 'bold'
    },
    iconMessage: {
        textAlign: 'center',
        paddingTop: '35px'
    },
    '@global': {
        '.MuiAvatar-root': {
            width: '70px',
            height: '70px'
        }
    }
}));

export default function ListFriend(props) {
    const classes = useStyles();
    const [listFollwers, setListFollwers] = useState();
    const [listFollwings, setListFollwings] = useState();
    useEffect(() => {
        userService.getFollwer(props.userId).then(res => {
            console.log(res.data.data.friendList[0].followers);
            setListFollwers(res.data.data.friendList[0].followers);
        }).catch(err => [
            console.log(err)
        ])
        userService.getFollwing(props.userId).then(res => {
            console.log(res.data.data.friendList[0].following);
            setListFollwings(res.data.data.friendList[0].following);
        }).catch(err => [
            console.log(err)
        ])
    }, [])
    return (
        <div>
            <Typography>Followers</Typography>
            <List className={classes.root}>
                {
                    listFollwers ? listFollwers.map((ele) => {
                        return <Card
                        >
                            <CardContent>
                                <Grid container>
                                    <Grid item xs={2} className={classes.nonePadding}>
                                        <Avatar
                                            onClick={
                                                () => {
                                                    console.log('sdfsd')
                                                    history.push({
                                                        pathname: "/profile/" + ele.user._id,
                                                    });
                                                    history.go();
                                                }
                                            }
                                            src={ele.user.photo} name={"Zoe"} size="lg" />
                                    </Grid>
                                    <Grid item xs={8} className={classes.nonePadding}>
                                        <Typography variant="h6" className={classes.title} component="h6">
                                            {ele.user.firstname + ele.user.lastname}
                                        </Typography>
                                        <Typography className={classes.pos} variant="body2" color="textSecondary">
                                            {ele.user.address}
                                        </Typography>
                                        <Typography variant="body2" component="p">
                                            99 người theo dõi
                                    </Typography>

                                    </Grid>
                                    <Grid item xs={2} className={classes.iconMessage} >
                                        <MessageIcon ></MessageIcon>
                                    </Grid>
                                </Grid>
                            </CardContent>
                        </Card>
                        // <ListItem alignItems="flex-start">
                        //     <ListItemAvatar>
                        //         <Avatar className={classes.avatarImg}  alt="Remy Sharp" src={ele.user.photo} />
                        //     </ListItemAvatar>
                        //     <ListItemText
                        //         primary={ele.user.firstname}
                        //         secondary={
                        //             <React.Fragment>
                        //                 <Typography
                        //                     component="span"
                        //                     variant="body2"
                        //                     className={classes.inline}
                        //                     color="textPrimary"
                        //                 >
                        //                     {ele.user.address}
                        //                 </Typography>
                        //             </React.Fragment>
                        //         }
                        //     />
                        // </ListItem>
                    }) : null
                }
            </List>
            <Typography>Followings</Typography>
            <List className={classes.root}>
                {
                    listFollwings ? listFollwings.map((ele) => {
                        return <Card
                            onClick={
                                () => {
                                    history.push({
                                        pathname: "/profile/" + ele.user._id,
                                    });
                                    history.go();
                                }
                            }>
                            <CardContent>
                                <Grid container>
                                    <Grid item xs={2} className={classes.nonePadding}>
                                        <Avatar src={ele.user.photo} name={"Zoe"} size="lg" />
                                    </Grid>
                                    <Grid item xs={8} className={classes.nonePadding}>
                                        <Typography variant="h6" className={classes.title} component="h6">
                                            {ele.user.firstname + ele.user.lastname}
                                        </Typography>
                                        <Typography className={classes.pos} variant="body2" color="textSecondary">
                                            {ele.user.address}
                                        </Typography>
                                        <Typography variant="body2" component="p">
                                            99 người theo dõi
                                    </Typography>

                                    </Grid>
                                    <Grid item xs={2} className={classes.iconMessage} >
                                        <MessageIcon ></MessageIcon>
                                    </Grid>
                                </Grid>
                            </CardContent>
                        </Card>
                    }) : null
                }
            </List>
        </div>
    );
}
