import axios from "axios";
import { authHeader } from "../helpers/auth-header";
export const notificatonService = {
    readNotifications,
    fetchNotifications
  };
  

function logout() {
    localStorage.removeItem("user")
}
function readNotifications(notificationIds) {
    const requestOptions = {
        method: "POST",
        headers: 
            authHeader(),
        body: JSON.stringify({
            notificationIds
        })
    }
    return fetch("/api/notification/readNotifications/", requestOptions);
}
function fetchNotifications(queryOptions) {
    console.log('service notification')
    const requestOptions = {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            Authorization: JSON.parse(localStorage.getItem("user")).token
        },
        body: JSON.stringify({
            ...queryOptions
        })
    };

    return axios.get("http://localhost:8000/api/notifications/getNotifications/", requestOptions);
}
