import React, { useEffect, useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import {
    ConversationHeader,
    Avatar,
    VoiceCallButton,
    VideoCallButton,
    ChatContainer,
    TypingIndicator,
    MessageSeparator,
    EllipsisButton,
    MessageList,
    Message,
    MessageInput,
} from "@chatscope/chat-ui-kit-react";

const useStyles = makeStyles((theme) => ({
    formMessage: {
        height: "750px",
        position: "relative"
    },
    nonePadding: {
        padding: "0!important"
    }
}));

export function MessagePage() {
    const [messageInputValue, setMessageInputValue] = useState("");
    function onSubmitMessage(val) {
        console.log(val)
    }
    useEffect(() => {

    }, [])
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    return (
        <ChatContainer>
            <ConversationHeader>
                <ConversationHeader.Back />
                <Avatar src={avatarImg} name="Zoe" />
                <ConversationHeader.Content userName="Zoe" info="Active 10 mins ago" />
                <ConversationHeader.Actions>
                    <VoiceCallButton />
                    <VideoCallButton />
                    <EllipsisButton orientation="vertical" />
                </ConversationHeader.Actions>
            </ConversationHeader>
            <MessageList typingIndicator={<TypingIndicator content="Zoe is typing" />}>
                <MessageSeparator content="Saturday, 30 November 2019" />
                <Message model={{
                    message: "Hello my friend",
                    sentTime: "15 mins ago",
                    sender: "Zoe",
                    direction: "incoming",
                    position: "single"
                }}>
                    <Avatar src={avatarImg} name="Zoe" />
                </Message>
                <Message model={{
                    message: "Hello my friend",
                    sentTime: "15 mins ago",
                    sender: "Patrik",
                    direction: "outgoing",
                    position: "single"
                }} avatarSpacer />
                <Message model={{
                    message: "Hello my friend",
                    sentTime: "15 mins ago",
                    sender: "Zoe",
                    direction: "incoming",
                    position: "first"
                }} avatarSpacer />
            </MessageList>
            <MessageInput placeholder="Type message here" value={messageInputValue} onChange={val => setMessageInputValue(val)} onSend={(val) => onSubmitMessage(val)} />
        </ChatContainer>
    );
}
