import React, { useEffect, Fragment, useState } from 'react';
import { Container, Toolbar } from '@material-ui/core';
import { PostRestaurant } from '../../components/post/postRestaurant';
import { postRestaurantService } from './../../services/postRestaurant.service';
import PropTypes from 'prop-types';
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { SideBar } from './../../components/sidebar/Sidebar';
import { makeStyles } from "@material-ui/core/styles";
import { Card, CardActionArea, CardContent, CardMedia, Divider, Typography } from '@material-ui/core';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import { restaurantService } from '../../services/restaurant.service';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import { history } from '../../helpers/history';
import { Rate } from 'antd';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Box from '@material-ui/core/Box';
function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}
const useStyles = makeStyles((theme) => ({
    restaurantList: {
        width: '47%',
        marginRight: '10px',
        minWidth: '300px',
        marginBottom: '10px',
        height: '300px'
    },
    media: {
        height: 140,
    },
    '@global': {
        '#listRestaurant .MuiListItem-gutters': {
            display: "block"
        }
    }
}));

export function Place(props) {
    const classes = useStyles();
    const [data, setData] = useState();
    function reviewRestaurant(value) {
        console.log("hihiihihih");
        console.log(value);
    }
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    useEffect(() => {
        restaurantService.getAllRestaurant()
            .then((res) => { console.log(res); setData(res.data.data.restaurant) })
            .catch(err => console.log(err));
    }, [])
    return (
        <Container style={{ backgroundColor: "#FAFAFA" }}>
            <Toolbar></Toolbar>
            <Tabs
                // orientation="vertical"
                variant="scrollable"
                value={value}
                onChange={handleChange}
                aria-label="Vertical tabs example"
                className={classes.tabs}
            >
                <Tab label="Item One" {...a11yProps(0)} />
                <Tab label="Item Two" {...a11yProps(1)} />
            </Tabs>
            <Grid container spacing={3} >
                <Grid item xs={12} className={classes.nonePadding}>
                    <Header></Header>
                </Grid>
                <Grid item xs={3}>
                    <SideBar></SideBar>
                </Grid>
                <Grid item xs={9}>
                    <TabPanel value={value} index={0}>
                        <Container>
                            <Fragment>
                                <h1>Đánh giá các nhà hàng</h1>
                                <Divider></Divider>
                                <List id="listRestaurant">
                                    {console.log(data)}
                                    {data ?
                                        data.map(ele => {
                                            return <Card onClick={() => { history.push("/restaurant/" + ele._id) }} className={classes.restaurantList} style={{ float: 'left' }}>
                                                <CardActionArea>
                                                    <CardMedia
                                                        className={classes.media}
                                                        image="https://www.planetware.com/wpimages/2020/02/france-in-pictures-beautiful-places-to-photograph-eiffel-tower.jpg"
                                                        title="Contemplative Reptile"
                                                    />
                                                    <CardContent>
                                                        <Typography variant="h5" component="h2">
                                                            {
                                                                ele.restaurantname
                                                            }
                                                        </Typography>
                                                        <Typography gutterBottom variant="caption" component="h2">
                                                            {
                                                                ele.address
                                                            }
                                                        </Typography>
                                                        <Typography variant="body2" color="initial" component="p">
                                                            {
                                                                ele.description
                                                            }
                                                        </Typography>
                                                    </CardContent>
                                                </CardActionArea>
                                                <CardActions>
                                                    <Rate disabled allowHalf defaultValue={ele.rating} />
                                                </CardActions>
                                            </Card>
                                        })

                                        : ""}
                                </List>
                            </Fragment>

                        </Container >
                    </TabPanel>
                </Grid>
            </Grid>
        </Container>
    );
}
