import React, { useState, useEffect } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { SideBarSearch } from './../../components/sidebarSearch/SidebarSearch';
import { Post } from '../../components/post/Post';
import { Card, CardActionArea, CardMedia, Container, List } from '@material-ui/core';
import { postService } from '../../services/post.service';
import { searchService } from '../../services/search.service';
import { history } from '../../helpers/history';
const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    nonePadding: {
        padding: "0!important"
    },
    people: {
        marginTop: '100px'
    },
    title: {
        fontSize: 16,
        fontWeight: 'bold'
    },
    iconMessage: {
        textAlign: 'center',
        paddingTop: '35px'
    }
}));

export function Search(props) {
    const dataSearch = props.match.params.description;
    const classes = useStyles();
    const [dataPost, setDataPost] = useState();
    const [restaurantList, setRestaurantList] = useState();
    let mounted = true;
    async function searchRes(searchKey) {
        let data = await searchService.searchRestaurant(searchKey);
        setRestaurantList(data);
    }
    useEffect(() => {
        postService.getSearchPost(dataSearch)
            .then(items => {
                if (mounted) {
                    setDataPost(items);
                }
            })
        searchRes(dataSearch);
        // dispatch(socketAction.connect());
        return () => mounted = false;
    }, []);
    return (
        <Container>
            <Grid container spacing={3} >
                <Grid item xs={12} className={classes.nonePadding}>
                    <Header></Header>
                </Grid>
                <Grid item xs={3}>
                    <SideBarSearch></SideBarSearch>
                </Grid>
                <Grid item xs={8} className={classes.people}>
                    {/* <PeopleSearch></PeopleSearch> */}
                    {
                        
                        < List id="searchRestaurant">
                            {restaurantList ?
                                restaurantList.map(ele => {
                                    return <Card onClick={() => { history.push("/restaurant/" + ele._id) }}style={{ float: 'left' }}>
                                        <CardActionArea>
                                            <CardMedia
                                                className={classes.media}
                                                image="https://www.planetware.com/wpimages/2020/02/france-in-pictures-beautiful-places-to-photograph-eiffel-tower.jpg"
                                                title="Contemplative Reptile"
                                            />
                                        </CardActionArea>
                                    </Card>
                                })

                                : ""}
                        </List>
                    }
            {

                    dataPost ? dataPost.map((value) => {
                        return <Post value={value} key={value._id} isShow={false} ></Post>;
                    }) : null
                }

                {/* <ImageList></ImageList>
                    <FriendList></FriendList>
                    <RestaurantNearMe></RestaurantNearMe> */}
            </Grid>
            </Grid >
        </Container >
    );
}
