import axios from "axios";
import { authHeader } from "../helpers/auth-header";
export const notificatonService = {
    readNotifications,
    fetchNotifications
};


function logout() {
    localStorage.removeItem("user")
}
function readNotifications(notificationIds) {
    // const requestOptions = {
    //     method: "POST",
    //     headers: authHeader(),
    //     body: JSON.stringify({
    //         notificationIds
    //     })
    // }
    console.log(requestOptions)
    return axios.post("http://localhost:8000/api/notifications/readNotifications/", { notificationIds: notificationIds }, { headers: authHeader() });
}
function fetchNotifications(queryOptions) {
    console.log('service notification')
    const requestOptions = {
        headers: authHeader(),
        body: JSON.stringify({
            ...queryOptions
        })
    };

    return axios.get("http://localhost:8000/api/notifications/getNotifications/", requestOptions);
}
