import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { postService } from './../../services/post.service';
import { Post } from '../../components/post/Post';
import axios from 'axios';
import { authHeader } from '../../helpers/auth-header';
import { GridList, GridListTile } from '@material-ui/core';
import { Card } from '@material-ui/core';
const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        width: '100%',
        height: "100%",
    },
}));
export function ImgOfUser(props) {
    const classes = useStyles();
    const [imgList, setImg] = useState([]);
    let mounted = true;

    useEffect(() => {
        axios.get('http://localhost:8000/api/users/getImageOfUser/image', { headers: authHeader() })
            .then((result) => {
                if (mounted) {
                    var list=[];
                    result.data.restaurant.forEach(ele=>{
                        list.concat(ele.photo);
                    })
                    setImg(list);
                }
            }).catch(err => {
                console.log(err)
            })
        return () => mounted = false;
    }, []);
    return (
        <div>
            <GridList cellHeight={160} className={classes.gridList} col={3}>
                {imgList.length!=0 ? imgList.map((ele) => 
                    <GridListTile key={ele} cols={1}>
                        <img src={ele} alt={ele} />
                    </GridListTile>
                ): ''}
            </GridList>
        </div>
    );
}
