import { locationConstants } from '../constants/location.constants';
var initialState={
    location: []
};
export function location(state = initialState, action) {
  switch (action.type) {
    case locationConstants.SET_LOCATION:
      return { location: [action.location[0], action.location[1]] };
    default:
      return state
  }
}
