import React, { useState } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Badge from '@material-ui/core/Badge';
import { red } from '@material-ui/core/colors';
import { Avatar, Grid, Paper } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import PhotoCamera from '@material-ui/icons/PhotoCamera';
import { useSelector } from 'react-redux';
import { Modal, Button, Upload } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import ImgCrop from 'antd-img-crop';
const useStyles = makeStyles((theme) => ({
    large: {
        width: "100%",
        height: "100%",
        margin: "auto",
    },
    root: {
        maxWidth: "100%",
        maxHeight: "100%",
        height: "100%",
        boxShadow: 'none'
    },
    input: {
        display: 'none',
    },
    '@global': {
        '.MuiAvatar-circle': {
            border: "1px solid"
        }
    }
}));
const SmallAvatar = withStyles((theme) => ({
    root: {
        width: 35,
        height: 35,
        border: `2px solid ${theme.palette.background.paper}`,
    },
}))(Avatar);
export function CoverImage(props) {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);
    const data = props.userInfo;
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };
    const [isVisibleChangeAvatarModal, setIsVisibleChangeAvatarModal] = useState(false);
    const handleOk = () => {
        setIsVisibleChangeAvatarModal(false);
    };

    const handleCancel = () => {
        setIsVisibleChangeAvatarModal(false);
    };
    return (
        <Card className={classes.root}>
            <Paper style={{ backgroundImage: '', backgroundRepeat: "no-repeat", backgroundSize: "cover", height: "90%", postion: "relative" }}>
                <div>
                    <form onSubmit={(value) => console.log(value)}>
                        <input accept="image/*" className={classes.input} id="icon-button-file" type="file" />
                        <label htmlFor="icon-button-file" title="chỉnh sửa ảnh bìa">
                            <IconButton color="primary" aria-label="upload picture" component="span">
                                <PhotoCamera />
                            </IconButton>
                        </label>
                    </form>
                </div>
                <div style={{ width: "20%", top: "0", margin: "auto", paddingTop: "0%" }}>
                    <Paper style={{ boxShadow: "none", width: "100%", borderRadius: "50%", margin: "auto", border: '1px' }}>
                        <Badge
                            overlap="circle"
                            anchorOrigin={{
                                vertical: 'bottom',
                                horizontal: 'right',
                            }}
                            badgeContent={<SmallAvatar title="cập nhật ảnh đại diện" alt="Change avatar" onClick={e => { }}>
                                <PhotoCamera onClick={e => { setIsVisibleChangeAvatarModal(!isVisibleChangeAvatarModal) }}></PhotoCamera>
                                <Modal
                                    title="Cập nhật ảnh đại diện"
                                    visible={isVisibleChangeAvatarModal}
                                    onOk={handleOk} onCancel={handleCancel}
                                    className="location_modal"
                                    footer={[]}
                                >
                                    <ImgCrop
                                        rotate
                                        modalOk={(e)=>{console.log(e)}}
                                    >
                                        <Upload >
                                            <Button icon={<UploadOutlined />}>Click to Upload</Button>
                                        </Upload>
                                    </ImgCrop>
                                    <div>
                                        Ảnh đã tải lên
                                    </div>
                                </Modal>
                            </SmallAvatar>}
                        >
                            <Avatar alt="" src={data ? data.user.photo : null} className={classes.large} />
                        </Badge>
                    </Paper>
                </div>
            </Paper>
        </Card >
    );
}
