import React, { useEffect, useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { Container, Divider } from '@material-ui/core';
import { MessengerSidePanel } from './messengerSidePanel';
import { MessContent } from './messegerContent';
import styles from "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css";
import {
    MainContainer,
    ConversationHeader,
    Avatar,
    VoiceCallButton,
    VideoCallButton,
    ChatContainer,
    ExpansionPanel,
    TypingIndicator,
    MessageSeparator,
    Sidebar,
    Search,
    EllipsisButton,
    ConversationList,
    InfoButton,
    Conversation,
    MessageList,
    Message,
    MessageInput,
} from "@chatscope/chat-ui-kit-react";
import { useSelector } from 'react-redux';

const useStyles = makeStyles((theme) => ({
    formMessage: {
        height: "750px",
        position: "relative"
    },
    nonePadding: {
        padding: "0!important"
    }
}));

export function MessagePage() {
    const classes = useStyles();
    const roomId= useSelector(state=>state)
    function onSubmitMessage(val) {
        console.log(val)
    }



    useEffect(() => {
        
        //get chat room
    }, [])
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    return (
        <Container>
            <Grid container spacing={3} >
                <Grid item xs={12} className={classes.nonePadding}>
                    <Header></Header>
                </Grid>
                <Grid className={classes.formMessage} item xs={12} style={{ marginTop: "80px" }}>
                    <MainContainer responsive>
                        <MessengerSidePanel></MessengerSidePanel>
                        <MessContent></MessContent>
                        <Sidebar position="right">
                            <ExpansionPanel open title="INFO">
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                            </ExpansionPanel>
                            <ExpansionPanel title="LOCALIZATION">
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                            </ExpansionPanel>
                            <ExpansionPanel title="MEDIA">
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                            </ExpansionPanel>
                            <ExpansionPanel title="SURVEY">
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                            </ExpansionPanel>
                            <ExpansionPanel title="OPTIONS">
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                                <p>Lorem ipsum</p>
                            </ExpansionPanel>
                        </Sidebar>
                    </MainContainer>
                </Grid>
            </Grid>
        </Container >
    );
}
