import React, { useEffect, useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import { Card } from 'antd';
import { List, Avatar } from 'antd';
import axios from 'axios';
import { authHeader } from '../../helpers/auth-header';

const useStyles = makeStyles((theme) => ({

}));


export default function RestaurantRecommend() {
    const [data,setData]=useState([]);
    const classes = useStyles();
    useEffect(()=>{
        axios.post('http://localhost:8000/api/restaurants/getRecommnedRestaurantListByLocation',{
            headers: authHeader(),
            location: {lat: 10,
            long: 110}
        }).then(res=>{
            setData(res.data)
        }).catch(err=>{
            console.log(err)
        })
    },[])
    
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    return (
        <Card size="small" title="Restaurant đề xuất" style={{ width: '100%' }}>
            <List
                itemLayout="horizontal"
                dataSource={data}
                renderItem={item => (
                    <List.Item>
                        <List.Item.Meta
                            avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                            title={<a href="https://ant.design">{data.restaurantname}</a>}
                            description="Ant Design, a desig"
                        />
                    </List.Item>
                )}
            />
        </Card>
    );
}
