import { authHeader } from '../helpers/auth-header';
import axios from 'axios';
export const postService = {
    getAllPost,
    deletePost,
    getPostByUser,
    getPostLikes
};
function getAllPost(description) {
    let header = authHeader();
    let config = {
        headers: header
    }
    
    return axios.get(`http://localhost:8000/api/posts?description=${description?description:''}`, config)
        .then(res =>
            res.data.data.data
        )
        .catch(error => console.log(error));

}
function deletePost(idPost) {
    let header = authHeader();
    let config = {
        headers: header
    }
    return axios.delete(`http://localhost:8000/api/posts/${idPost}`, config);

}
function getPostByUser(idUser) {
    let header = authHeader();
    let config = {
        headers: header
    }
    console.log(idUser);
    return axios.get(`http://localhost:8000/api/posts/getPostByUser/${idUser}`, config)
        .then(res =>
            res.data.data.data
        )
        .catch(error => console.log(error));
}

function getPostLikes(idPost) {
    let header = authHeader();
    let config = {
        headers: header
    }
    return axios.get(`http://localhost:8000/api/posts/getPostLikes/${idPost}`, config)
        .then(res =>
            res.data.data.data
        )
        .catch(error => console.log(error));
}
