import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';

import { red } from '@material-ui/core/colors';
import { Avatar, Grid, Paper } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    large: {
        position: "relative",
        width: "100%",
        height: "100%",
        margin: "auto",

    },
    root: {
        maxWidth: "100%",
        maxHeight: "100%",
        height: "100%",
    },
    media: {
        height: 0,
        // paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

export function CoverImage() {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return (
        <Card className={classes.root} style={{ backgroundImage: 'url(/images/Octocat.png)' }}>
            <div style={{ width: "20%", postion: "absolute",top: "0", margin: "auto" , paddingTop: "10%", }}>
                <Paper style={{ boxShadow: "none", height: "50%" }}>
                    <Avatar alt="Remy Sharp" src="/images/Octocat.png" className={classes.large} />
                </Paper>
            </div>
        </Card>
    );
}
