import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
// import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Chat from '@material-ui/icons/Chat';
import Like from '@material-ui/icons/ThumbUpAlt';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Divider from '@material-ui/core/Divider';
import styles from "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css";
import {
  MessageInput,
  ConversationList,
  Conversation,
  Avatar
} from "@chatscope/chat-ui-kit-react";
const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: "100%",

  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

export function Post() {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);
  var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar aria-label="recipe" src={avatarImg} className={classes.avatar}>
          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title="Shrimp and Chorizo Paella"
        subheader="September 14, 2016"
      />
      <CardContent>
        <Typography variant="body2" component="p">
          This impressive paella is a perfect party dish and a fun meal to cook together with your
          guests. Add 1 cup of frozen peas along with the mussels, if you like.
        </Typography>
      </CardContent>
      <CardMedia
        className={classes.media}
        image="/images/Octocat.png"
        title="Paella dish"
      />

      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="add to favorites">
          <Chat />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <MessageInput />
          <div style={{ height: '250px' }} >
            <ConversationList>

              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
              <Conversation name="Lilly" info="Yes i can do it for you">
                <Avatar src={avatarImg}>
                </Avatar>
              </Conversation>
            </ConversationList>
          </div>
        </CardContent>
      </Collapse>
      {/* <Divider /> */}

    </Card>
  );
}
