import React, { useEffect, useState } from 'react';
import 'antd/dist/antd.css';
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import CssBaseline from '@material-ui/core/CssBaseline';
import { Header } from '../../components/header/Header';
import { SideBar } from './../../components/sidebar/Sidebar';
import { Mess } from './../../components/sidebar/Mess';
import { Post } from '../../components/post/Post';
import { Container } from '@material-ui/core';
import {WhatDoYouThing} from './../../components/whatdoyouthing/WhatDoYouThing';
import axios from 'axios';
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  nonePadding: {
    padding: "0!important"
  }
}));
function getAllPost(){
  axios.get(`http://localhost:8000/api/posts`,config)
  .then(res => {
    data=res.data.data;
  })
  .catch(error => console.log(error));
};
export function HomePage() {
  const  [data, setData]=useState([]);
  const classes = useStyles();
  let config = {
    headers: {
      'Authorization': 'Bearer ' + "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYwNzEwYTdlNzRkZTQyMGNmY2MwYjBjZiIsImlhdCI6MTYxODAyMjI4NCwiZXhwIjoxNjI1Nzk4Mjg0fQ.rSMaSvb1BslDaEkrZP2J0hCCQ_tWe1k_8WC9doK4mGg"
    }
  }
  useEffect(() => {
    let mounted = true;
    getPost()
     .then(items => {
       if(mounted) {
         setData(items)
       }
     })
    return () => mounted = false;
  });

  return (
    <Container>
      <Grid container spacing={3} >
        <Grid item xs={12} className={classes.nonePadding}>
          <Header></Header>
        </Grid>
        <Grid item xs={3}>
          <SideBar></SideBar>
        </Grid>
        <Grid item xs={6}>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <WhatDoYouThing></WhatDoYouThing>
          <div id='postList'></div>
          {
            data.map((value,idx) => {
               return <Post value={value}></Post>;
            })
          }
          <Post></Post>
          <Post></Post>
          <Post></Post>
          <Post></Post>
        </Grid>
        <Grid item xs={3}>
          <Mess></Mess>
        </Grid>
      </Grid>
    </Container>
  );
}
