import { authHeader } from '../helpers/auth-header';
import axios from 'axios';
export const postService = {
    getAllPost,
    deletePost,
    getPostByUser,
    getPostLikes,
    likePost
};
function getAllPost(description) {
    let header = authHeader();
    let config = {
        headers: header
    }
    return axios.get(`http://localhost:8000/api/posts`, config);
}
function deletePost(idPost) {
    let header = authHeader();
    let config = {
        headers: header
    }
    return axios.delete(`http://localhost:8000/api/posts/${idPost}`, config);

}
function getPostByUser(idUser) {
    let header = authHeader();
    let config = {
        headers: header
    }
    return axios.get(`http://localhost:8000/api/posts/getPostByUser/${idUser}`, config)
        .then(res =>
            res.data.data.data
        )
        .catch(error => console.log(error));
}

function getPostLikes(idPost) {
    let header = authHeader();
    let config = {
        headers: header
    }
    return axios.get(`http://localhost:8000/api/posts/getPostLikes/${idPost}`, config)
        .then(res =>
            res.data.data.data
        )
        .catch(error => console.log(error));
}

function likePost(idPost){
    return axios({
        method: 'post',
        url: 'http://localhost:8000/api/posts/likePost',
        data: {
            "postId": idPost,
        },
        headers: authHeader()
    })
        .then((response) => {
            return response
        }, (error) => {
            console.log(error);
        });
}
