import { makeStyles } from '@material-ui/core/styles';
import React, { useState } from 'react';
import { Modal, Button, Form, Input, DatePicker } from 'antd';
import { useDispatch, useSelector } from 'react-redux';

const useStyles = makeStyles((theme) => ({
  form: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '72ch',
    },
    width: '600px'
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function ChangeInfomationModal() {

  const classes = useStyles();
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 16 },
  };
  /* eslint-disable no-template-curly-in-string */
  const validateMessages = {
    required: '${label} is required!',
    types: {
      email: '${label} is not a valid email!',
      number: '${label} is not a valid number!',
    },
    number: {
      range: '${label} must be between ${min} and ${max}',
    },
  };
  /* eslint-enable no-template-curly-in-string */
  const userInfo = useSelector(state => state.authentication.user.data.user);
  const onFinish = (values) => {
    console.log(values);
  };
  const formItemLayout = {
    labelCol: { span: 5 },
    wrapperCol: { span: 19 },
  };
  return (
    <div>
      <Button type="primary" onClick={showModal}>
        Chỉnh sửa
      </Button>
      <Modal title="Chỉnh sửa thông tin cá nhân" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel} footer={null}>
        <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
          <Form.Item {...formItemLayout} name={['firstname']} label="Tên" rules={[{ required: true }]} >
            <Input defaultValue={userInfo.firstname} name="form_name"/>
          </Form.Item>
          <Form.Item {...formItemLayout} name={['lastname']} label="Họ" rules={[{ required: true }]} >
            <Input defaultValue={userInfo.lastname} value={userInfo.lastname}/>
          </Form.Item>
          <Form.Item  {...formItemLayout} name={[ 'birthday']} label="Ngày sinh" >
            <DatePicker />
          </Form.Item>
          <Form.Item  {...formItemLayout} name={['address']} label="Địa chỉ">
            <Input />
          </Form.Item>
          <Form.Item  {...formItemLayout} name={['email']} label="Email" rules={[{ type: 'email',required: true }]}>
            <Input  defaultValue={userInfo.email}/>
          </Form.Item>
          <Form.Item {...formItemLayout} name={['phonenumber']} label="Số điện thoại">
            <Input />
          </Form.Item>
          <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 7 }}>
            <Button type="primary" htmlType="submit">
              Chỉnh sửa
        </Button>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
}
