import React, { useState, useEffect } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { Header } from '../../components/header/Header';
import { SideBarSearch } from './../../components/sidebarSearch/SidebarSearch';
import { Post } from '../../components/post/Post';
import { Card, CardActionArea, CardContent, CardMedia, Container, List, Typography } from '@material-ui/core';
import { postService } from '../../services/post.service';
import { searchService } from '../../services/search.service';
import { history } from '../../helpers/history';
import Tab from '@material-ui/core/Tab';
import Tabs from '@material-ui/core/Tabs';
const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    nonePadding: {
        padding: "0!important"
    },
    people: {
        marginTop: '100px'
    },
    title: {
        fontSize: 16,
        fontWeight: 'bold'
    },
    iconMessage: {
        textAlign: 'center',
        paddingTop: '35px'
    },
    restaurantList: {
        width: '47%',
        marginRight: '10px',
        minWidth: '300px',
        marginBottom: '10px',
        height: '300px'
    }, media: {
        height: 140,
    }, tabs: {
        borderRight: `1px solid ${theme.palette.divider}`,
    },
    '@global': {
        '#listRestaurant .MuiListItem-gutters': {
            display: "block"
        }
    }
}));
function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}
TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
  };
  
  function a11yProps(index) {
    return {
      id: `vertical-tab-${index}`,
      'aria-controls': `vertical-tabpanel-${index}`,
    };
  }
export function Search(props) {
    const dataSearch = props.match.params.description;
    const classes = useStyles();
    const [dataPost, setDataPost] = useState();
    const [restaurantList, setRestaurantList] = useState();
    const [userList, setUserList] = useState();
    let mounted = true;
    async function searchRes(searchKey) {
        let data = await searchService.searchRestaurant(searchKey);
        setRestaurantList(data);
    }
    async function searchUser(searchKey) {
        let data = await searchService.searchUser(searchKey);
        console.log(data)
        setUserList(data);
    }
    useEffect(() => {
        postService.getSearchPost(dataSearch)
            .then(items => {
                if (mounted) {
                    setDataPost(items);
                }
            })
        searchRes(dataSearch);
        searchUser(dataSearch);
        // dispatch(socketAction.connect());
        return () => mounted = false;
    }, []);
    return (
        <Container>
            <Grid container spacing={3} >
                <Grid item xs={12} className={classes.nonePadding}>
                    <Header></Header>
                </Grid>
                <Grid item xs={3}>
                    {/* <SideBarSearch></SideBarSearch> */}

                    <Tabs
                        orientation="vertical"
                        variant="scrollable"
                        value={value}
                        onChange={handleChange}
                        aria-label="Vertical tabs example"
                        className={classes.tabs}
                    >
                        <Tab label="Item One" {...a11yProps(0)} />
                        <Tab label="Item Two" {...a11yProps(1)} />
                        <Tab label="Item Three" {...a11yProps(2)} />
                        <Tab label="Item Four" {...a11yProps(3)} />
                        <Tab label="Item Five" {...a11yProps(4)} />
                        <Tab label="Item Six" {...a11yProps(5)} />
                        <Tab label="Item Seven" {...a11yProps(6)} />
                    </Tabs>
                </Grid>
                <Grid item xs={8} className={classes.people}>
                    <h1>Restaurant</h1>
                    {
                        restaurantList ? restaurantList.map(ele => {
                            return <Card minWidth="100%" onClick={() => history.push('/restaurant/' + ele._id)}>
                                <CardContent >
                                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                                        Nhà Hàng
                                    </Typography>
                                    <Typography variant="h5" component="h2">{ele._source.restaurantname}</Typography>
                                </CardContent>
                            </Card>
                        }) : ''
                    }
                    <h1>User</h1>
                    {
                        userList ? userList.map(ele => {
                            return <Card minWidth="100%" onClick={() => history.push('/profile/' + ele._id)}>
                                <CardContent >
                                    <Typography variant="h5" component="h2">{ele.firstname + ' ' + ele.lastname}</Typography>
                                </CardContent>
                            </Card>
                        }) : ''
                    }
                    <h1>Post</h1>
                    {

                        dataPost ? dataPost.map((value) => {
                            return <Post value={value} key={value._id} isShow={false} ></Post>;
                        }) : null
                    }

                    {/* <ImageList></ImageList>
                    <FriendList></FriendList>
                    <RestaurantNearMe></RestaurantNearMe> */}
                </Grid>
            </Grid >
        </Container >
    );
}
