import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import InboxIcon from '@material-ui/icons/Inbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import { useDispatch, useSelector } from 'react-redux';
import { userActions } from './../../actions/user.actions';
import { history } from './../../helpers/history';
const useStyles = makeStyles((theme) => ({
  root: {
    width: '300px',
    backgroundColor: theme.palette.background.paper,
  },
}));

// function ListItemLink(props) {
//   return <ListItem button component="a" {...props} />;
// }
// userService.getInfoUser(idUser._id).then((res) => {
//   history.push({
//     pathname: "/profile/" + idUser._id,
//     state: {userInfo: res.data.data, user: idUser._id}
//   });
//   history.go();
// }).catch(err => console.log(err));

export default function DropdownUser(props) {
  const classes = useStyles();
  const dispatch=useDispatch();
  const logout = () => {
    dispatch(userActions.logout);
  };
  const idUser = useSelector(state=>state.authentication.user.data.user);
  return (
    <div className={classes.root}>
      <List component="nav" aria-label="main mailbox folders">
        <ListItem button onClick={()=>{history.push("/profile/"+idUser)}}>
          <ListItemIcon>
            <DraftsIcon />
          </ListItemIcon>
          <ListItemText primary="Xem trang cá nhân" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <InboxIcon />
          </ListItemIcon>
          <ListItemText primary="Cài đặt" onClick={()=>{history.push(idUser?"/setting/"+idUser._id:'') }}  />
        </ListItem>
        <Divider />
        <ListItem button>
          <ListItemIcon>
            <DraftsIcon />
          </ListItemIcon>
          <ListItemText primary="Đăng xuất" onClick={logout} />
        </ListItem>

      </List>
    </div>
  );
}
