import React, { useEffect, useState } from 'react';
import styles from "@chatscope/chat-ui-kit-styles/dist/default/styles.min.css";
import {
    Avatar,
    Sidebar,
    Search,
    ConversationList,
    Conversation,
} from "@chatscope/chat-ui-kit-react";
import { useDispatch, useSelector } from 'react-redux';
import { chatAction } from '../../actions/chat.action';

export function MessengerSidePanel() {
    const dispatch = useDispatch();
    const chatRoomList = useSelector(state => state.chat.rooms);
    const roomId=useSelector(state=>state.chat.roomId);
    console.log(chatRoomList)
    function changeRoom(room){
        dispatch(chatAction.changeRoom(room))
    }   

    useEffect(() => {
        dispatch(chatAction.getChatRooms());
    }, [])
    var avatarImg = "https://chatscope.io/storybook/react/static/media/zoe.e31a4ff8.svg";
    return (
        <Sidebar position="left" scrollable={false}>
            <Search placeholder="Search..." />
            <ConversationList>
                {
                    chatRoomList ? chatRoomList.map((ele) => {
                        return <Conversation name={ele.members[0].firstname+ele.members[0].lastname} lastSenderName="Lilly" info="Yes i can do it for you" onClick={(e)=>{
                            //set room 
                            changeRoom(ele)
                        }}>
                            <Avatar src={avatarImg} name={ele.members[0]} status="available" />
                        </Conversation>

                    }) : null

                }
            </ConversationList>
        </Sidebar>
    );
}
