import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';

import { red } from '@material-ui/core/colors';
import { Avatar, Grid, Paper } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    large: {
        position: "relative",
        width: "100%",
        height: "100%",
        margin: "auto",
    },
    root: {
        maxWidth: "100%",
        maxHeight: "100%",
        height: "100%",
        boxShadow: 'none'
    },
}));

export function CoverImage() {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return (
        <Card className={classes.root}>
            <Paper style={{ backgroundImage: 'url(/images/blue.jpg)' , backgroundRepeat: "no-repeat",backgroundSize: "cover", height: "90%", postion: "relative" }}>
                <div style={{ width: "20%", postion: "absolute", top: "0", margin: "auto", paddingTop: "3%" }}>
                    <Paper style={{ boxShadow: "none", width: "100%", borderRadius: "50%", margin:"auto" }}>
                        <Avatar alt="avatar" src="/images/Octocat.png" className={classes.large} />
                    </Paper>
                </div>
            </Paper>
        </Card>
    );
}
