import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import { restaurantService } from '../../services/restaurant.service';
const useStyles = makeStyles({
    root: {
        minWidth: 275,

    },
    bullet: {
        display: 'inline-block',
    },
    title: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    pos: {
        marginBottom: 12,
    },
});

export function IntroduceRestaurant(props) {
    const userInfo = props.userInfo;
    const restaurantId = props.restaurantIdFollow;
    const [restaurantInfo, setRestaurantInfo] = useState();
    const [isFollow, setIsFollow] = props.followRestaurant;
    const classes = useStyles();
    // const bull = <span className={classes.bullet}>•</span>;
    const [followNumber, setFollowNumber] = useState(userInfo !== undefined ? userInfo.user.followers.followers.length : 0);
    const [address, setAddress] = useState(userInfo !== undefined ? userInfo.user.address : '');
    useEffect(() => {
        restaurantService.getRestaurantById(restaurantId)
            .then(items => {
                setRestaurantInfo(items.data.data.data);
            })
        setFollowNumber(userInfo !== undefined ? userInfo.user.followers.followers.length : 0);
        setAddress(userInfo !== undefined ? userInfo.user.address : '');
    }, [isFollow, userInfo]);
    return (
        <Card className={classes.root} variant="outlined">
            {console.log(restaurantInfo)}
            <CardContent >
                <Typography className={classes.title}>
                    Giới thiệu
                </Typography>
            </CardContent>

                {restaurantInfo ?
                            <List><ListItem >
                    <ListItemIcon>
                        <SendIcon />
                    </ListItemIcon>
                    <ListItemText primary={"Địa chỉ:  " + restaurantInfo ? restaurantInfo.address : ''} />
                </ListItem>
                <ListItem >
                    <ListItemIcon>
                        <SendIcon />
                    </ListItemIcon>
                    <ListItemText primary={"Số điện thoại:  " + restaurantInfo ? restaurantInfo.phone : ''} />
                </ListItem>
                <ListItem >
                    <ListItemIcon>
                        <SendIcon />
                    </ListItemIcon>
                    <ListItemText primary={"Email:  " + restaurantInfo ? restaurantInfo.email : ''} />
                </ListItem>
                <ListItem >
                    <ListItemIcon>
                        <DraftsIcon />
                    </ListItemIcon>
                    <ListItemText primary={"Có " + followNumber + " người theo dõi"} />
                </ListItem>
                </List>:''}
        </Card>
    );
}
